import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TakeAway = () => {
  const [categories, setCategories] = useState([]);
  const [dishes, setDishes] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [categoriesRes, dishesRes] = await Promise.all([
        axios.get('http://localhost:5000/api/categories'),
        axios.get('http://localhost:5000/api/dishes')
      ]);

      setCategories(categoriesRes.data);
      setDishes(dishesRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data');
      setLoading(false);
    }
  };

  const addToCart = (dish) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === dish.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === dish.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevCart, { ...dish, quantity: 1 }];
    });
  };

  const updateQuantity = (dishId, quantity) => {
    if (quantity < 1) {
      removeFromCart(dishId);
      return;
    }
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === dishId
          ? { ...item, quantity }
          : item
      )
    );
  };

  const removeFromCart = (dishId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== dishId));
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + (parseFloat(item.price) * item.quantity), 0);
  };

  const handlePlaceOrder = async () => {
    if (cart.length === 0) {
      alert('Please add items to your order');
      return;
    }

    try {
      const orderData = {
        customer_name: 'Guest',
        order_type: 'takeaway',
        items: cart.map(item => ({
          dish_id: item.id,
          quantity: item.quantity,
          price: item.price,
          notes: ''
        })),
        total_amount: calculateTotal()
      };

      await axios.post('http://localhost:5000/api/orders', orderData);
      alert('Order placed successfully!');
      setCart([]);
    } catch (error) {
      console.error('Error placing order:', error);
      alert('Failed to place order');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-red-500 text-center p-4">
        {error}
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="mb-6">
        <h1 className="text-2xl font-bold">Takeaway Orders</h1>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Menu Section */}
        <div className="lg:col-span-2">
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold mb-4">Menu</h2>
            <div className="space-y-4">
              {categories.map(category => (
                <div key={category.id}>
                  <h3 className="font-semibold mb-2">{category.name}</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {dishes
                      .filter(dish => dish.category_id === category.id && dish.available)
                      .map(dish => (
                        <div key={dish.id} className="flex justify-between items-center">
                          <div>
                            <h3 className="font-medium">{dish.name}</h3>
                            <p className="text-sm text-gray-500">{dish.description}</p>
                          </div>
                          <div className="text-right">
                            <p className="font-medium">₹{parseFloat(dish.price).toFixed(2)}</p>
                            <button
                              onClick={() => addToCart(dish)}
                              className="mt-1 text-sm bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                            >
                              Add to Cart
                            </button>
                          </div>
                        </div>
                      ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Cart Section */}
        <div className="lg:col-span-1">
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-semibold mb-4">Your Order</h2>
            <div className="space-y-4">
              {cart.map(item => (
                <div key={item.id} className="flex justify-between items-center">
                  <div>
                    <p className="font-medium">{item.name}</p>
                    <p className="text-sm text-gray-500">₹{parseFloat(item.price).toFixed(2)}</p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => updateQuantity(item.dish_id, item.quantity - 1)}
                      className="text-gray-600 hover:text-gray-900"
                    >
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button
                      onClick={() => updateQuantity(item.dish_id, item.quantity + 1)}
                      className="text-gray-600 hover:text-gray-900"
                    >
                      +
                    </button>
                    <button
                      onClick={() => removeFromCart(item.dish_id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
              {cart.length === 0 && (
                <p className="text-gray-500">Your cart is empty</p>
              )}
              {cart.length > 0 && (
                <>
                  <div className="flex justify-between items-center pt-4 border-t">
                    <span className="text-lg font-semibold">Total:</span>
                    <span className="text-xl font-bold">₹{parseFloat(calculateTotal()).toFixed(2)}</span>
                  </div>
                  <button
                    onClick={handlePlaceOrder}
                    className="w-full py-2 px-4 bg-indigo-600 text-white rounded hover:bg-indigo-700"
                  >
                    Place Order
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TakeAway;
